mapper.select:
SELECT

FROM area a

WHERE a.id = #{id}

SELECT

FROM area a


    a.del_flag = #{DEL_FLAG_NORMAL}

        AND a.ft_area_id=#{ftAreaId}


        AND a.tyfo_area_id=#{tyfoAreaId}

SELECT

FROM area a


    a.del_flag = #{DEL_FLAG_NORMAL}



        ORDER BY ${page.orderBy}


        ORDER BY a.update_date DESC

SELECT

FROM area a


    a.del_flag = #{DEL_FLAG_NORMAL}



        ORDER BY ${page.orderBy}


        ORDER BY a.update_date DESC

mapper.update:
UPDATE area SET
del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
